    renderForm = (timeTitle) => {
        const {values, handleChange} = this.props;
        let inputs = []
        timeTitle.map(time => {
            let timeInputs = formConfig.filter(input => input.time === time);
            timeInputs.map(input => inputs.push(input))
        })
        // const timeInputs_0 = formConfig.filter(input => input.time === timeTitle[0]);
        // const timeInputs_1 = formConfig.filter(input => input.time === timeTitle[1]);
        // const timeInputs_2 = formConfig.filter(input => input.time === timeTitle[2]);
        // timeInputs_0.map(input => inputs.push(input));
        // timeInputs_1.map(input => inputs.push(input));
        // timeInputs_2.map(input => inputs.push(input));
        console.log('inputs', inputs)

        let formGridStyle = 'FormGrid';
        if (timeTitle === 'infinitive') {
            formGridStyle = 'FormGridInfinitive';

        }
        return <div className={classes[formGridStyle]}>
            {/*TO DO make it more generic*/}
            {/*SVG images*/}
            {/*{this.renderFormSvg(timeTitle)}*/}
            {/*Mapping inputs*/}
            {inputs.map((input, index) => {
                const {rowStart, rowEnd, columnStart, columnEnd, time, label, placeholder, name, iconSrc, iconAlt} = input;
                return (
                    <div className={classes.FormGridItem} style={{
                        gridRow: `${rowStart}/${rowEnd}`,
                        gridColumn: `${columnStart}/${columnEnd}`
                    }}
                         key={index}
                    >
                        <CustomInput
                            name={name}
                            inputLabel={label}
                            id={name}
                            placeholder={placeholder}
                            onChange={handleChange(name)}
                            value={values[name]}
                            defaultValue={''}
                        />

                        {/*<img className={classes.Icon} src={iconSrc}*/}
                        {/*       alt={iconAlt}/>*/}

                    </div>
                )
            })}
        </div>
    }


    render() {

        return (
            <Fragment>
                {this.renderForm(["infinitive", "present", "imperative"])}
                {/*{this.renderForm("present")}*/}
                {/*{this.renderForm("imperative")}*/}
                <div className={classes.ButtonSection}><Button
                    variant="contained"
                    color="primary"
                    onClick={this.continue}>Continue</Button></div>
            </Fragment>
        );
    }
}

export default FormInfinitive;

