import React, {Component, Fragment} from 'react';
import Button from "@material-ui/core/Button";
import classes from "../FormInfinitive/FormInfinitive.module.css"
import ManSign from "../../../icons/mars.svg";
import WomanSign from "../../../icons/female.svg";
import SingularSign from "../../../icons/user.svg";
import TextField from "@material-ui/core/TextField";
import MagenDavid from "../../../icons/jewish.svg";
import PluralSign from "../../../icons/group.svg";

class FormPast extends Component {
    continue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };

    back = (e) => {
        e.preventDefault();
        this.props.prevStep();
    };

    render() {
        const {values, handleChange} = this.props;
        return (
            <Fragment>
                <div className={classes.TimeTitle}>
                    <span>Past Tense</span>
                </div>


                    <div className={classes.GenderSignRow}>
                        <img className={classes.Icon} src={ManSign} alt="Man Sign"/>
                        <img className={classes.Icon} src={WomanSign} alt="Woman Sign"/>
                    </div>


                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>
                            <span>1 st</span>
                        </div>
                        <div className={classes.IconBox}>
                            {/*<img className={classes.Icon} src={MagenDavid} alt="Jewish Sign"/>*/}
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Man'
                            fullWidth
                            // label="Man"
                            value={values.past_man_1st}
                            onChange={handleChange('past_man_1st')}
                            defaultValue={values.past_man_1st}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>
                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Woman'
                        fullWidth
                        // label="Woman"
                        value={values.past_woman_1st}
                        onChange={handleChange('past_woman_1st')}
                        defaultValue={values.past_woman_1st}
                        size="small"
                    /></div>
                </div>

                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>
                            {/*<span>1 st</span>*/}
                        </div>
                        <div className={classes.IconBox}>
                            {/*<img className={classes.Icon} src={MagenDavid} alt="Jewish Sign"/>*/}
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Transcription'
                            fullWidth
                            // label="Transcription"
                            value={values.past_man_1st_transcription}
                            onChange={handleChange('past_man_1st_transcription')}
                            defaultValue={values.past_man_1st_transcription}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        // label="Transcription"
                        value={values.past_woman_1st_transcription}
                        onChange={handleChange('past_woman_1st_transcription')}
                        defaultValue={values.past_woman_1st_transcription}
                        size="small"
                    /></div>
                </div>

                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>
                            <span>2 st</span>
                        </div>
                        <div className={classes.IconBox}>
                            <img className={classes.Icon} src={SingularSign} alt="User Sign"/>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Man'
                            fullWidth
                            // label="Man"
                            value={values.past_man_singular_2nd}
                            onChange={handleChange('past_man_singular_2nd')}
                            defaultValue={values.past_man_singular_2nd}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Woman'
                        fullWidth
                        // label="Woman"
                        value={values.past_woman_singular_2nd}
                        onChange={handleChange('past_woman_singular_2nd')}
                        defaultValue={values.past_woman_singular_2nd}
                        size="small"
                    /></div>
                </div>
                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>
                        </div>
                        <div className={classes.IconBox}>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Transcription'
                            fullWidth
                            // label="Transcription"
                            value={values.past_man_singular_2nd_transcription}
                            onChange={handleChange('past_man_singular_2nd_transcription')}
                            defaultValue={values.past_man_singular_2nd_transcription}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        // label="Transcription"
                        value={values.past_woman_singular_2nd_transcription}
                        onChange={handleChange('past_woman_singular_2nd_transcription')}
                        defaultValue={values.past_woman_singular_2nd_transcription}
                        size="small"
                    /></div>
                </div>
                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>

                        </div>
                        <div className={classes.IconBox}>
                            <img className={classes.UsersIcon} src={PluralSign} alt="Group Sign"/>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Men'
                            fullWidth
                            // label="Men"
                            value={values.past_man_plural_2nd}
                            onChange={handleChange('past_man_plural_2nd')}
                            defaultValue={values.past_man_plural_2nd}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Women'
                        fullWidth
                        // label="Women"
                        value={values.past_woman_plural_2nd}
                        onChange={handleChange('past_woman_plural_2nd')}
                        defaultValue={values.past_woman_plural_2nd}
                        size="small"
                    /></div>
                </div>
                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>

                        </div>
                        <div className={classes.IconBox}>

                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Transcription'
                            fullWidth
                            // label="Transcription"
                            value={values.past_man_plural_2nd_transcription}
                            onChange={handleChange('past_man_plural_2nd_transcription')}
                            defaultValue={values.past_man_plural_2nd_transcription}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        // label="Transcription"
                        value={values.past_woman_plural_2nd_transcription}
                        onChange={handleChange('past_woman_plural_2nd_transcription')}
                        defaultValue={values.past_woman_plural_2nd_transcription}
                        size="small"
                    /></div>
                </div>
                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>
                            <span>3 st</span>
                        </div>
                        <div className={classes.IconBox}>
                            <img className={classes.Icon} src={SingularSign} alt="User Sign"/>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Man'
                            fullWidth
                            // label="Man"
                            value={values.past_man_singular_3nd}
                            onChange={handleChange('past_man_singular_3nd')}
                            defaultValue={values.past_man_singular_3nd}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Woman'
                        fullWidth
                        // label="Woman"
                        value={values.past_woman_singular_3nd}
                        onChange={handleChange('past_woman_singular_3nd')}
                        defaultValue={values.past_woman_singular_3nd}
                        size="small"
                    /></div>
                </div>
                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>

                        </div>
                        <div className={classes.IconBox}>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Transcription'
                            fullWidth
                            // label="Transcription"
                            value={values.past_man_singular_3nd_transcription}
                            onChange={handleChange('past_man_singular_3nd_transcription')}
                            defaultValue={values.past_man_singular_3nd_transcription}
                            size="small"
                        />
                    </div>

                    <div className={classes.IconBox}>

                    </div>


                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        // label="Transcription"
                        value={values.past_woman_singular_3nd_transcription}
                        onChange={handleChange('past_woman_singular_3nd_transcription')}
                        defaultValue={values.past_woman_singular_3nd_transcription}
                        size="small"
                    /></div>
                </div>

                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>

                        </div>
                        <div className={classes.IconBox}>
                            <img className={classes.UsersIcon} src={PluralSign} alt="Group Sign"/>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='People'
                            fullWidth
                            // label="People"
                            value={values.past_plural_3nd}
                            onChange={handleChange('past_plural_3nd')}
                            defaultValue={values.past_plural_3nd}
                            size="small"
                        />
                    </div>


                </div>
                <div className={classes.TimeRow}>
                    <div className={classes.InfoBox}>
                        <div className={classes.Conjugation}>
                        </div>
                        <div className={classes.IconBox}>
                        </div>

                    </div>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Transcription'
                            fullWidth
                            label="Transcription"
                            value={values.past_plural_3nd_transcription}
                            onChange={handleChange('past_plural_3nd_transcription')}
                            defaultValue={values.past_plural_3nd_transcription}
                            size="small"
                        />
                    </div>


                </div>

                <div className={classes.ButtonSection}>
                    <Button
                        onClick={this.continue}
                        variant="contained"
                        color="primary">Continue</Button>
                    <Button
                        onClick={this.back}
                        variant="contained">Back</Button>
                </div>


            </Fragment>
        )
    }
}

export default FormPast;
