import React, {Component, Fragment} from 'react';
import {ThemeProvider as MuiThemeProvider} from '@material-ui/core/styles'
import CustomInput from "../FormElements/CustomInput/CustomInput";
import Button from '@material-ui/core/Button';
import classes from './FormInfinitive.module.css';
import formInputConfig from '../formInputsConfiguration';

import MagenDavid from '../../../icons/jewish.svg';
import Globe from '../../../icons/translate.svg';
import ManSign from '../../../icons/mars.svg';
import WomanSign from '../../../icons/female.svg';
import SingularSign from '../../../icons/user.svg';
import PluralSign from '../../../icons/group.svg';

class FormInfinitive extends Component {
    continue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };

    renderForm = (timeTitle) => {
        const {values, handleChange} = this.props;
        const timeInputs = formInputConfig.filter(input => input.time === timeTitle);
        return <div className={classes.FormGrid}>
            {/*TO DO make it more generic*/}
            {/*SVG images*/}

                 <div className={classes.FormGridItem}>
                     <img className={classes.Icon} src={ManSign}
                          style={{
                              gridRow: '1/2',
                              gridColumn: '2/3'
                          }}

                          alt="Man Sign"/>
                 </div>
            <img className={classes.Icon} src={WomanSign}
                 style={{
                     gridRow: '1/2',
                     gridColumn: '3/4'
                 }}
                 alt="Man Sign"/>
            <img className={classes.Icon} src={SingularSign}
                 style={{
                     gridRow: '2/3',
                     gridColumn: '1/2'
                 }}
                 alt="Man Sign"/>
            <img className={classes.Icon} src={PluralSign}
                 style={{
                     gridRow: '4/5',
                     gridColumn: '1/2'
                 }}
                 alt="Man Sign"/>

            {/*Mapping inputs*/}
            {timeInputs.map((input, index) => {
                const {rowStart, rowEnd, columnStart, columnEnd, time, label, placeholder, name} = input;
                return (
                    <div className={classes.FormGridItem} style={{
                        gridRow: `${rowStart}/${rowEnd}`,
                        gridColumn: `${columnStart}/${columnEnd}`
                    }}
                         key={index}
                    >
                        <CustomInput
                            name={name}
                            inputLabel={label}
                            id={name}
                            placeholder={placeholder}
                            onChange={handleChange(name)}
                            value={values[name]}
                            defaultValue={''}
                        />
                    </div>
                )
            })}
        </div>
    }


    render() {

        return (
            <Fragment>
                {this.renderForm("present")}
                <div className={classes.ButtonSection}><Button
                    variant="contained"
                    color="primary"
                    onClick={this.continue}>Continue</Button></div>
            </Fragment>
        );
    }
}

export default FormInfinitive;

