import React, {Component, Fragment} from 'react';
// import moduleName from 'material-ui/styles/';
import {ThemeProvider as MuiThemeProvider} from '@material-ui/core/styles'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import classes from './FormInfinitive.module.css';
import MagenDavid from '../../../icons/jewish.svg';
import Globe from '../../../icons/translate.svg';
import ManSign from '../../../icons/mars.svg';
import WomanSign from '../../../icons/female.svg';
import SingularSign from '../../../icons/user.svg';
import PluralSign from '../../../icons/group.svg';

class FormInfinitive extends Component {
    continue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };

    render() {
        const {values, handleChange} = this.props;
        return (
            <Fragment>
                <div className={classes.InfinitiveRow}>
                    <img className={classes.Icon} src={MagenDavid} alt="Jewish Sign"/>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Infinitive'
                            fullWidth
                            label="Infinitive"
                            value={values.infinitive}
                            onChange={handleChange('infinitive')}
                            defaultValue={values.infinitive}
                            size="small"
                        />
                    </div>

                    <img className={classes.Icon} src={Globe} alt="Globe Sign"/>

                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        label="Transcription"
                        value={values.infinitive}
                        onChange={handleChange('infinitive')}
                        defaultValue={values.infinitive}
                        size="small"
                    /></div>
                </div>

                <div className={classes.TimeTitle}>
                    <span>Imperative</span>
                </div>

                <div className={classes.ImperativeRow}>
                    <div className={classes.GenderSignRow}>
                        <img className={classes.Icon} src={ManSign} alt="Jewish Sign"/>
                        <img className={classes.Icon} src={WomanSign} alt="Jewish Sign"/>
                    </div>
                </div>

                <div className={classes.TimeRow}>
                    <img className={classes.Icon} src={SingularSign} alt="User Sign"/>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Infinitive'
                            fullWidth
                            label="Infinitive"
                            value={values.infinitive}
                            onChange={handleChange('infinitive')}
                            defaultValue={values.infinitive}
                            size="small"
                        />
                    </div>

                    {/*<img className={classes.Icon} src={Globe} alt="Globe Sign"/>*/}

                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        label="Transcription"
                        value={values.infinitive}
                        onChange={handleChange('infinitive')}
                        defaultValue={values.infinitive}
                        size="small"
                    /></div>
                </div>

                <div className={classes.TimeRow}>
                    <img className={classes.Icon} src={SingularSign} alt="User Sign"/>

                    <div className={classes.Input}>
                        <TextField
                            placeholder='Infinitive'
                            fullWidth
                            label="Infinitive"
                            value={values.infinitive}
                            onChange={handleChange('infinitive')}
                            defaultValue={values.infinitive}
                            size="small"
                        />
                    </div>

                    {/*<img className={classes.Icon} src={Globe} alt="Globe Sign"/>*/}

                    <div className={classes.Input}><TextField
                        placeholder='Transcription'
                        fullWidth
                        label="Transcription"
                        value={values.infinitive}
                        onChange={handleChange('infinitive')}
                        defaultValue={values.infinitive}
                        size="small"
                    /></div>
                </div>


                <div className={classes.ButtonSection}><Button
                    variant="contained"
                    color="primary"
                    onClick={this.continue}>Continue</Button></div>
            </Fragment>
        );
    }
}

export default FormInfinitive;

