import React, {Component, Fragment} from 'react';
// import moduleName from 'material-ui/styles/';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import classes from './FormInfinitive.module.css';

class FormInfinitive extends Component {
    continue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };

    render() {
        const {values, handleChange} = this.props;
        return (
            <MuiThemeProvider>
                <Fragment>
                    <p>Looks like its FormInfinitive</p>
                    <div className={classes.InfinitiveBox}>
                        <TextField
                            placeholder='infinitive'
                            label="infinitive"
                            value={values.infinitive}
                            onChange={handleChange('infinitive')}
                            defaultValue={values.infinitive}
                        />
                    </div>
                    <div className={classes.ImperativeBox}>
                        <p><i>Imperative</i></p>
                        <div className={classes.Columns}>
                            <div className={classes.LeftColumn}>
                                <div className={classes.GenderSign}>

                                </div>

                                <div className={classes.Singular}>

                                </div>

                                <div className={classes.Plural}>

                                </div>

                            </div>
                            <div className={classes.RightColumn}>

                            </div>
                        </div>
                    </div>
                    <div className={classes.PresentBox}>

                    </div>


                    <Button
                        variant="contained"
                        color="primary"
                        onClick={this.continue}>Continue</Button>
                </Fragment>
            </MuiThemeProvider>
        );
    }
}

export default FormInfinitive;
