import React, {Component} from 'react';
import FormInfinitive from '../../components/WordForms/FormInfinitive/FormInfinitive';
import FormPast from '../../components/WordForms/FormPast/FormPast';
import FormFuture from '../../components/WordForms/FormFuture/FormFuture';
import FormConfirm from "../../components/WordForms/FormConfirm/FormConfirm";

class FormAddWord extends Component {
    state = {
        step: 1,
        infinitive: '',
        imperative_man_singular: '',
        imperative_man_plural: '',
        imperative_woman_singular: '',
        imperative_woman_plural: '',
        present_man_singular: '',
        present_man_plural: '',
        present_woman_singular: '',
        present_woman_plural: '',
        past_man_1st: '',
        past_woman_1st: '',
        past_man_singular_2nd: '',
        past_man_plural_2nd: '',
        past_woman_singular_2nd: '',
        past_woman_plural_2nd: '',
        past_man_singular_3nd: '',
        past_woman_singular_3nd: '',
        past_plural_3nd: '',
        future_man_1st: '',
        future_woman_1st: '',
        future_man_singular_2nd: '',
        future_man_plural_2nd: '',
        future_woman_singular_2nd: '',
        future_woman_plural_2nd: '',
        future_man_singular_3nd: '',
        future_man_plural_3nd: '',
        future_woman_singular_3nd: '',
        future_woman_plural_3nd: ''
    };

    nextStep = () => {
        const {step} = this.state;
        this.setState({step: step + 1})
    };

    prevStep = () => {
        const {step} = this.state;
        this.setState({step: step + 1})
    };

    // Handle field change
    handleChange = input => e => {
        this.setState({[input]: e.target.value});
    };

    render() {
        const {step} = this.state;
        const {
            infinitive,
            imperative_man_singular,
            imperative_man_plural,
            imperative_woman_singular,
            imperative_woman_plural,
            present_man_singular,
            present_man_plural,
            present_woman_singular,
            present_woman_plural,
            past_man_1st,
            past_woman_1st,
            past_man_singular_2nd,
            past_man_plural_2nd,
            past_woman_singular_2nd,
            past_woman_plural_2nd,
            past_man_singular_3nd,
            past_woman_singular_3nd,
            past_plural_3nd,
            future_man_1st,
            future_woman_1st,
            future_man_singular_2nd,
            future_man_plural_2nd,
            future_woman_singular_2nd,
            future_woman_plural_2nd,
            future_man_singular_3nd,
            future_man_plural_3nd,
            future_woman_singular_3nd,
            future_woman_plural_3nd
        } = this.state;

        const values = {
            infinitive,
            imperative_man_singular,
            imperative_man_plural,
            imperative_woman_singular,
            imperative_woman_plural,
            present_man_singular,
            present_man_plural,
            present_woman_singular,
            present_woman_plural,
            past_man_1st,
            past_woman_1st,
            past_man_singular_2nd,
            past_man_plural_2nd,
            past_woman_singular_2nd,
            past_woman_plural_2nd,
            past_man_singular_3nd,
            past_woman_singular_3nd,
            past_plural_3nd,
            future_man_1st,
            future_woman_1st,
            future_man_singular_2nd,
            future_man_plural_2nd,
            future_woman_singular_2nd,
            future_woman_plural_2nd,
            future_man_singular_3nd,
            future_man_plural_3nd,
            future_woman_singular_3nd,
            future_woman_plural_3nd
        };

        switch (step) {
            case 1:
                return (
                    <FormInfinitive
                        nextStep={this.nextStep}
                        handleChange={this.handleChange}
                        values={values}
                    />
                );
            case 2:
                return (
                    <FormPast/>
                );
            case 3:
                return (
                    <FormFuture/>
                );
            case 4:
                return (
                    <FormConfirm/>
                );
            default:
                return null
        }
    }
}


export default FormAddWord;
