import React, {Component, Fragment} from 'react';
// import moduleName from 'material-ui/styles/';
import { ThemeProvider as MuiThemeProvider } from '@material-ui/core/styles'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import classes from './FormInfinitive.module.css';
import MagenDavid from './jewish.svg';

class FormInfinitive extends Component {
    continue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };

    render() {
        const {values, handleChange} = this.props;
        return (
                <Fragment>
                   <div className={classes.InfinitiveRow}>

                   </div>


                    <div className={classes.ButtonSection}><Button
                        variant="contained"
                        color="primary"
                        onClick={this.continue}>Continue</Button></div>
                </Fragment>
        );
    }
}

export default FormInfinitive;

