import React, {Component, Fragment} from 'react';
import Button from "@material-ui/core/Button";
import classes from "../FormInfinitive/FormInfinitive.module.css";
import ManSign from "../../../icons/mars.svg";
import WomanSign from "../../../icons/female.svg";
import MagenDavid from "../../../icons/jewish.svg";
import TextField from "@material-ui/core/TextField";
import SingularSign from "../../../icons/user.svg";
import PluralSign from "../../../icons/group.svg";
import formInputConfig from "../formInputsConfiguration";
import CustomInput from "../FormElements/CustomInput/CustomInput";

class FormFuture extends Component {
    continue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };

    back = (e) => {
        e.preventDefault();
        this.props.prevStep();
    };

    renderСonjugations = () => {
        return (
            <Fragment>

                <div className={classes.ConjugationTitle}
                     style={{
                         gridRow: '2/3',
                         gridColumn: '1/2'
                     }}
                >
                    1 st
                </div>
                <div className={classes.ConjugationTitle}
                     style={{
                         gridRow: '4/5',
                         gridColumn: '1/2'
                     }}
                >
                    2 st
                </div>
                <div className={classes.ConjugationTitle}
                     style={{
                         gridRow: '8/9',
                         gridColumn: '1/2'
                     }}
                >
                    3 st
                </div>
            </Fragment>
        )
    }

    renderForm = (timeTitle) => {
        const {values, handleChange} = this.props;
        const timeInputs = formInputConfig.filter(input => input.time === timeTitle);
        return <div className={classes.FormGridPast}>
            {/*TO DO make it more generic*/}
            {/*SVG images*/}
            {this.renderFormSvg(timeTitle)}
            {this.renderСonjugations()}
            {/*Mapping inputs*/}
            {timeInputs.map((input, index) => {
                const {rowStart, rowEnd, columnStart, columnEnd, time, label, placeholder, name} = input;
                return (
                    <div className={classes.FormGridItem} style={{
                        gridRow: `${rowStart}/${rowEnd}`,
                        gridColumn: `${columnStart}/${columnEnd}`
                    }}
                         key={index}
                    >
                        <CustomInput
                            name={name}
                            inputLabel={label}
                            id={name}
                            placeholder={placeholder}
                            onChange={handleChange(name)}
                            value={values[name]}
                            defaultValue={''}
                        />
                    </div>
                )
            })}
        </div>

    }

    render() {
        const {values, handleChange} = this.props;
        return (
            <Fragment>

                <div className={classes.ButtonSection}>
                    <Button
                        onClick={this.continue}
                        variant="contained"
                        color="primary">Continue</Button>
                    <Button
                        onClick={this.back}
                        variant="contained">Back</Button>
                </div>
            </Fragment>
        )
    }
}

export default FormFuture;
